import React from 'react';
import { pure, skinnable } from 'revenge';
import OriginalTooltip from '../../../../../../../src/Tooltip';

import './tooltip.scss';

@pure
@skinnable()
export default class Tooltip extends React.Component {

  getLocals() {
    const { popover, children, ...other } = this.props;
    return {
      children,
      props: {
        ...other,
        popover: {
          attachToBody: true,
          ...popover
        }
      }
    };
  }

  template({ children, props }) {
    return (
      <OriginalTooltip { ...props }>
        {children}
      </OriginalTooltip>
    );
  }

}
